function exportToWord() {
    const examIndex = document.getElementById('report-exam-select').value;
    if (examIndex === '') {
        alert('L√ºtfen √∂nce bir sƒ±nav se√ßin.');
        return;
    }

    const exam = appState.exams[parseInt(examIndex)];
    const content = document.getElementById('report-content').innerHTML;

    // A4 optimize edilmi≈ü kompakt stil
    const fullContent = `
        <!DOCTYPE html>
        <html>
        <head>
            <meta charset="utf-8">
            <title>${exam.class} ${exam.semester}. D√ñNEM ${exam.name}</title>
            <style>
                @page { size: A4; margin: 0.5cm; }
                body { 
                    font-family: Arial, sans-serif; 
                    margin: 0;
                    font-size: 9px;
                    line-height: 1.1;
                }
                table { 
                    border-collapse: collapse; 
                    width: 100%; 
                    page-break-inside: avoid;
                }
                th, td { 
                    border: 1px solid #000; 
                    padding: 2px; 
                    text-align: center;
                    font-size: 8px;
                }
                th { 
                    background-color: #f0f0f0;
                    font-weight: bold;
                }
                .student-name { text-align: left; }
                .header-title {
                    text-align: center;
                    font-size: 11px;
                    font-weight: bold;
                    margin-bottom: 5px;
                }
                .score-legend {
                    font-size: 8px;
                    margin-top: 5px;
                }
            </style>
        </head>
        <body>
            ${content}
        </body>
        </html>
    `;

    // Cordova/Mobil cihaz kontrol√º
    const isCordova = (typeof window.cordova !== 'undefined');
    const isMobile = /Android|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);

    if (isCordova) {
        // Cordova ortamƒ±nda ger√ßek dosya kaydetme
        const fileName = `${exam.class}_${exam.semester}_DONEM_${exam.name}_SINAV_RAPORU.doc`;

        if (window.cordova && window.cordova.file) {
            const blob = new Blob([fullContent], { type: 'application/msword' });

            window.resolveLocalFileSystemURL(cordova.file.externalDataDirectory, function(dirEntry) {
                dirEntry.getFile(fileName, {create: true, exclusive: false}, function(fileEntry) {
                    fileEntry.createWriter(function(fileWriter) {
                        fileWriter.onwriteend = function() {
                            alert('Rapor ba≈üarƒ±yla kaydedildi!');
                            if (window.cordova.plugins && window.cordova.plugins.fileOpener2) {
                                cordova.plugins.fileOpener2.open(
                                    fileEntry.toURL(),
                                    'application/msword',
                                    {
                                        error: function(e) {
                                            alert('Dosya a√ßƒ±lamadƒ±. ƒ∞ndirilenler klas√∂r√ºnden bulabilirsiniz.');
                                        },
                                        success: function() {}
                                    }
                                );
                            }
                        };
                        fileWriter.onerror = function(e) {
                            alert('Dosya kaydedilemedi: ' + e.toString());
                        };
                        fileWriter.write(blob);
                    });
                }, function(e) {
                    alert('Dosya olu≈üturulamadƒ±: ' + e.toString());
                });
            }, function(e) {
                alert('Klas√∂re eri≈üilemedi: ' + e.toString());
            });
        } else {
            const blob = new Blob([fullContent], { type: 'text/html' });
            const url = window.URL.createObjectURL(blob);
            const a = document.createElement('a');
            a.href = url;
            a.download = `${exam.class}_${exam.semester}_DONEM_${exam.name}_SINAV_RAPORU.html`;
            a.style.display = 'none';
            document.body.appendChild(a);
            a.click();
            setTimeout(() => {
                document.body.removeChild(a);
                window.URL.revokeObjectURL(url);
            }, 100);
            alert('Rapor HTML formatƒ±nda indirildi!');
        }
    } else if (isMobile) {
        // Mobil tarayƒ±cƒ± i√ßin HTML dosyasƒ±
        const blob = new Blob([fullContent], { type: 'text/html' });
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `${exam.class}_${exam.semester}_DONEM_${exam.name}_SINAV_RAPORU.html`;
        a.style.display = 'none';
        document.body.appendChild(a);

        try {
            a.click();
            setTimeout(() => {
                document.body.removeChild(a);
                window.URL.revokeObjectURL(url);
            }, 100);
            alert('Rapor HTML formatƒ±nda indirildi!');
        } catch (e) {
            window.open(url, '_blank');
            alert('Rapor yeni pencerede a√ßƒ±ldƒ±.');
        }
    } else {
        // Masa√ºst√ºnde Word dosyasƒ± olarak indir
        const blob = new Blob([fullContent], { type: 'application/msword' });
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `${exam.class}_${exam.semester}_DONEM_${exam.name}_SINAV_RAPORU.doc`;
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        window.URL.revokeObjectURL(url);
        alert('Rapor ba≈üarƒ±yla indirildi!');
    }
}

// Enter tu≈üu ile form g√∂nderme
document.addEventListener('DOMContentLoaded', async function() {
