function createScoringTable(exam) {
    const container = document.getElementById('scoring-table-container');

    if (exam.students.length === 0) {
        container.innerHTML = '<p style="color: white; text-align: center;">Bu sƒ±nƒ±fta √∂ƒürenci bulunmuyor.</p>';
        return;
    }

    const headerCells = exam.criteriaWithDeadlines.map(c => 
        `<th style="min-width: 120px;">
            ${c.criteria}<br/>
            <small>(${c.deadline || 'Tarih yok'})<br/>(20 puan)</small>
        </th>`
    ).join('');

    const studentRows = exam.students.map(student => {
        const criteriaCells = exam.criteriaWithDeadlines.map(criteria => {
            const currentScore = exam.scores[student.id][criteria.criteria];
            return `
                <td>
                    <div class="score-buttons">
                        <button class="score-btn g ${currentScore === 10 ? 'selected' : ''}" 
                                onclick="setStudentScore(${student.id}, '${criteria.criteria}', 10)">G</button>
                        <button class="score-btn iyi ${currentScore === 15 ? 'selected' : ''}" 
                                onclick="setStudentScore(${student.id}, '${criteria.criteria}', 15)">ƒ∞Yƒ∞</button>
                        <button class="score-btn ci ${currentScore === 20 ? 'selected' : ''}" 
                                onclick="setStudentScore(${student.id}, '${criteria.criteria}', 20)">√áƒ∞</button>
                    </div>
                </td>
            `;
        }).join('');

        const totalScore = Object.values(exam.scores[student.id]).reduce((sum, score) => {
            return sum + (score || 0);
        }, 0);

        return `
            <tr>
                <td class="student-number">${student.number}</td>
                <td class="student-name">${student.name}</td>
                ${criteriaCells}
                <td class="total-score">${totalScore}</td>
            </tr>
        `;
    }).join('');

    container.innerHTML = `
        <table class="scoring-table">
            <thead>
                <tr>
                    <th>OKUL NO</th>
                    <th>AD SOYAD</th>
                    ${headerCells}
                    <th>TOPLAM</th>
                </tr>
            </thead>
            <tbody>
                ${studentRows}
            </tbody>
        </table>
    `;
}

function setStudentScore(studentId, criteria, score) {
    if (!appState.currentExam) return;

    // Sanitize criteria to prevent XSS
    const safeCriteria = criteria.replace(/'/g, "&#x27;");

    // Update score in current exam
    appState.currentExam.scores[studentId][criteria] = score;

    // Update score in stored exams
    const examIndex = appState.exams.findIndex(e => e.id === appState.currentExam.id);
    if (examIndex !== -1) {
        appState.exams[examIndex].scores[studentId][criteria] = score;
    }

    // Recreate the table to show updated scores
    createScoringTable(appState.currentExam);
    saveAppState();
}

function saveExamScores() {
    saveAppState();
    alert('Puanlar ba≈üarƒ±yla kaydedildi!');
}

function updateReportPreview() {
    const examIndex = document.getElementById('report-exam-select').value;
    const previewDiv = document.getElementById('report-preview');
    const contentDiv = document.getElementById('report-content');

    if (examIndex === '') {
        previewDiv.classList.add('hidden');
        return;
    }

    const exam = appState.exams[parseInt(examIndex)];
    if (!exam) return;

    const currentYear = new Date().getFullYear();
    const nextYear = currentYear + 1;

    const headerCells = exam.criteriaWithDeadlines.map(c => 
        `<th class="header-cell">
            ${c.criteria}<br/>
            ${c.deadline || '..../..../....'}
        </th>`
    ).join('');

    const scoringCells = exam.criteriaWithDeadlines.map(() => 
        `<th class="scoring-cell">
            <div class="score-marks">
                <span>G</span><span>ƒ∞Yƒ∞</span><span>√áƒ∞</span>
            </div>
        </th>`
    ).join('');

    const studentRows = exam.students.map(student => {
        const criteriaCells = exam.criteriaWithDeadlines.map(criteria => {
            const score = exam.scores[student.id][criteria.criteria];
            const gMark = score === 10 ? '‚óè' : '‚óã';
            const iyiMark = score === 15 ? '‚óè' : '‚óã';
            const ciMark = score === 20 ? '‚óè' : '‚óã';

            return `
                <td class="score-cell">
                    <div class="score-marks">
                        <span>${gMark}</span><span>${iyiMark}</span><span>${ciMark}</span>
                    </div>
                </td>
            `;
        }).join('');

        const totalScore = Object.values(exam.scores[student.id]).reduce((sum, score) => {
            return sum + (score || 0);
        }, 0);

        return `
            <tr>
                <td class="number-cell">${student.number}</td>
                <td class="name-cell">${student.name}</td>
                ${criteriaCells}
                <td class="total-cell">${totalScore}</td>
            </tr>
        `;
    }).join('');

    contentDiv.innerHTML = `
        <div class="report-title">
            ${currentYear} ‚Äì ${nextYear} Eƒûƒ∞Tƒ∞M √ñƒûRETƒ∞M YILI ${exam.school} ${exam.class} SINIFI ${exam.semester}. D√ñNEM ${exam.name}
        </div>

        <table class="report-table">
            <thead>
                <tr>
                    <th rowspan="2" class="main-header">OKUL NO</th>
                    <th rowspan="2" class="main-header">AD SOYAD</th>
                    ${headerCells}
                    <th rowspan="2" class="main-header">TOPLAM</th>
                </tr>
                <tr>
                    ${scoringCells}
                </tr>
            </thead>
            <tbody>
                ${studentRows}
            </tbody>
        </table>

        <div class="score-legend">
            <strong>G: 10 PUAN &nbsp;&nbsp; ƒ∞Yƒ∞: 15 PUAN &nbsp;&nbsp; √áƒ∞: 20 PUAN</strong>
        </div>
    `;

    previewDiv.classList.remove('hidden');
}

function exportToWord() {
    const examIndex = document.getElementById('report-exam-select').value;
    if (examIndex === '') {
        alert('L√ºtfen √∂nce bir sƒ±nav se√ßin.');
        return;
    }

    const exam = appState.exams[parseInt(examIndex)];
    const content = document.getElementById('report-content').innerHTML;

    // A4 optimize edilmi≈ü kompakt stil
    const fullContent = `
        <!DOCTYPE html>
        <html>
        <head>
            <meta charset="utf-8">
            <title>${exam.class} ${exam.semester}. D√ñNEM ${exam.name}</title>
            <style>
                @page { size: A4; margin: 0.5cm; }
                body { 
                    font-family: Arial, sans-serif; 
                    margin: 0;
                    font-size: 9px;
                    line-height: 1.1;
                }
                table { 
                    border-collapse: collapse; 
                    width: 100%; 
                    page-break-inside: avoid;
                }
                th, td { 
                    border: 1px solid #000; 
                    padding: 2px; 
                    text-align: center;
                    font-size: 8px;
                }
                th { 
                    background-color: #f0f0f0;
                    font-weight: bold;
                }
                .student-name { text-align: left; }
                .header-title {
                    text-align: center;
                    font-size: 11px;
                    font-weight: bold;
                    margin-bottom: 5px;
                }
                .score-legend {
                    font-size: 8px;
                    margin-top: 5px;
                }
            </style>
        </head>
        <body>
            ${content}
        </body>
        </html>
    `;

    // Cordova/Mobil cihaz kontrol√º
    const isCordova = (typeof window.cordova !== 'undefined');
    const isMobile = /Android|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);

    if (isCordova) {
        // Cordova ortamƒ±nda ger√ßek dosya kaydetme
        const fileName = `${exam.class}_${exam.semester}_DONEM_${exam.name}_SINAV_RAPORU.doc`;

        if (window.cordova && window.cordova.file) {
            const blob = new Blob([fullContent], { type: 'application/msword' });

            // Android i√ßin Downloads klas√∂r√ºne kaydet
            window.resolveLocalFileSystemURL(cordova.file.externalDataDirectory, function(dirEntry) {
                dirEntry.getFile(fileName, {create: true, exclusive: false}, function(fileEntry) {
                    fileEntry.createWriter(function(fileWriter) {
                        fileWriter.onwriteend = function() {
                            alert('Rapor ba≈üarƒ±yla kaydedildi!');
                            // Dosyayƒ± a√ß
                            if (window.cordova.plugins && window.cordova.plugins.fileOpener2) {
                                cordova.plugins.fileOpener2.open(
                                    fileEntry.toURL(),
                                    'application/msword',
                                    {
                                        error: function(e) {
                                            alert('Dosya a√ßƒ±lamadƒ±. ƒ∞ndirilenler klas√∂r√ºnden bulabilirsiniz.');
                                        },
                                        success: function() {
                                            // Dosya ba≈üarƒ±yla a√ßƒ±ldƒ±
                                        }
                                    }
                                );
                            }
                        };
                        fileWriter.onerror = function(e) {
                            alert('Dosya kaydedilemedi: ' + e.toString());
                        };
                        fileWriter.write(blob);
                    });
                }, function(e) {
                    alert('Dosya olu≈üturulamadƒ±: ' + e.toString());
                });
            }, function(e) {
                alert('Klas√∂re eri≈üilemedi: ' + e.toString());
            });
        } else {
            // Cordova file plugin'i mevcut deƒüilse fallback
            const blob = new Blob([fullContent], { type: 'text/html' });
            const url = window.URL.createObjectURL(blob);
            const a = document.createElement('a');
            a.href = url;
            a.download = `${exam.class}_${exam.semester}_DONEM_${exam.name}_SINAV_RAPORU.html`;
            a.style.display = 'none';
            document.body.appendChild(a);
            a.click();
            setTimeout(() => {
                document.body.removeChild(a);
                window.URL.revokeObjectURL(url);
            }, 100);
            alert('Rapor HTML formatƒ±nda indirildi!');
        }
    } else if (isMobile) {
        // Mobil tarayƒ±cƒ± i√ßin HTML dosyasƒ±
        const blob = new Blob([fullContent], { type: 'text/html' });
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `${exam.class}_${exam.semester}_DONEM_${exam.name}_SINAV_RAPORU.html`;
        a.style.display = 'none';
        document.body.appendChild(a);

        try {
            a.click();
            setTimeout(() => {
                document.body.removeChild(a);
                window.URL.revokeObjectURL(url);
            }, 100);
            alert('Rapor HTML formatƒ±nda indirildi!');
        } catch (e) {
            window.open(url, '_blank');
            alert('Rapor yeni pencerede a√ßƒ±ldƒ±.');
        }
    } else {
        // Masa√ºst√ºnde Word dosyasƒ± olarak indir
        const blob = new Blob([fullContent], { type: 'application/msword' });
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `${exam.class}_${exam.semester}_DONEM_${exam.name}_SINAV_RAPORU.doc`;
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        window.URL.revokeObjectURL(url);
        alert('Rapor ba≈üarƒ±yla indirildi!');
    }
}

// Enter tu≈üu ile form g√∂nderme
document.addEventListener('DOMContentLoaded', async function() {
    // Database + Local Storage'dan veriyi y√ºkle
    await loadAppState();

    // Enter tu≈üu event listeners
    document.getElementById('school-name').addEventListener('keypress', function(e) {
