package org.apache.cordova;

import java.security.Principal;
import java.security.PrivateKey;
import java.security.cert.X509Certificate;

import android.annotation.SuppressLint;
import android.webkit.ClientCertRequest;

/**
 * Implementation of the ICordovaClientCertRequest for Android WebView.
 *
 */
public class CordovaClientCertRequest implements ICordovaClientCertRequest {

    private final ClientCertRequest request;

    public CordovaClientCertRequest(ClientCertRequest request) {
        this.request = request;
    }

    /**
     * Cancel this request
     */
    @SuppressLint("NewApi")
    @Override
    public void cancel()
    {
        request.cancel();
    }

    /**
     * @return the host name of the server requesting the certificate.
     */
    @SuppressLint("NewApi")
    @Override
    public String getHost()
    {
        return request.getHost();
    }

    /**
     * @return the acceptable types of asymmetric keys (can be null).
     */
    @SuppressLint("NewApi")
    @Override
    public String[] getKeyTypes()
    {
        return request.getKeyTypes();
    }

    /**
     * @return the port number of the server requesting the certificate.
     */
    @SuppressLint("NewApi")
    @Override
    public int getPort()
    {
        return request.getPort();
    }

    /**
     * @return the acceptable certificate issuers for the certificate matching the private key (can be null).
     */
    @SuppressLint("NewApi")
    @Override
    public Principal[] getPrincipals()
    {
        return request.getPrincipals();
    }

    /**
     * Ignore the request for now. Do not remember user's choice.
     */
    @SuppressLint("NewApi")
    @Override
    public void ignore()
    {
        request.ignore();
    }

    /**
     * Proceed with the specified private key and client certificate chain. Remember the user's positive choice and use it for future requests.
     *
     * @param privateKey The privateKey
     * @param chain The certificate chain
     */
    @SuppressLint("NewApi")
    @Override
    public void proceed(PrivateKey privateKey, X509Certificate[] chain)
    {
        request.proceed(privateKey, chain);
    }
}

